'use strict';

var Logger = require('../Component/Logger.js');

class AppController {

    get(callback) {
        this.callback = callback;
        this.callback({'response' : 'HttpRequest GET'});
    }

    post(callback) {
        this.callback = callback;
        this.callback({'response' : 'HttpRequest POST'});
    }

    put(callback) {
        this.callback = callback;
        this.callback({'response' : 'HttpRequest PUT'});
    }

    delete(callback) {
        this.callback = callback;
        this.callback({'response' : 'HttpRequest DELETE'});
    }

    getLogger() {
        this.logger = new Logger();

        return this.logger;
    }
}

module.exports = AppController;
